import React from 'react';
import PropTypes from 'prop-types';
import { formatTemperature } from '../utils/helpers';

const HourlyForecast = ({ forecastData }) => {
  if (!forecastData) return <div className="loading">Loading forecast...</div>;

  // Get sorted dates from the forecast data
  const dates = Object.keys(forecastData).sort();
  console.log(forecastData)
  
  // Define the hours we want to display (3-hour intervals)
  const displayHours = ['00:00','03:00','06:00','09:00','12:00','15:00','18:00','21:00'];

  // Function to format date display
  const formatDisplayDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div className="hourly-forecast">
      <h2>5-Day Hourly Forecast</h2>
      <div className="forecast-grid">
        {/* Header row with hours */}
        <div className="grid-row header">
          <div className="grid-cell time-label">Time â†’</div>
          {displayHours.map(hour => (
            <div key={hour} className="grid-cell hour-header">{hour}</div>
          ))}
        </div>

        {/* Forecast rows for each day */}
        {dates.slice(0, 5).map(date => (
          <div key={date} className="grid-row">
            <div className="grid-cell date-label">
              {formatDisplayDate(date)}
            </div>
            
            {displayHours.map(hour => {
              const hourData = forecastData[date]?.[hour];
              
              if (!hourData) {
                return (
                  <div key={`${date}-${hour}`} className="grid-cell empty">
                    -
                  </div>
                );
              }

              return (
                <div key={`${date}-${hour}`} className="grid-cell">
                  <div className="temperature">
                    {formatTemperature(hourData.temperature)}
                  </div>
                  <div className="condition">
                    {hourData.condition}
                  </div>
                  <div className="wind">
                    {hourData.windSpeed} m/s
                  </div>
                  {hourData.humidity && (
                    <div className="humidity">
                      {hourData.humidity}%
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

HourlyForecast.propTypes = {
  forecastData: PropTypes.objectOf(
    PropTypes.objectOf(
      PropTypes.shape({
        temperature: PropTypes.number.isRequired,
        condition: PropTypes.string.isRequired,
        windSpeed: PropTypes.number.isRequired,
        humidity: PropTypes.number
      })
    )
  )
};

export default HourlyForecast;
