import React, { useState, useEffect } from 'react';
import { fetchCities, fetchWeather, logCitySelection } from './utils/api';
import CityDropdown from './components/CityDropdown';
import CurrentWeather from './components/CurrentWeather';
import HourlyForecast from './components/HourlyForecast';
import PopularCities from './components/PopularCities';
import useLocalStorage from './hooks/useLocalStorage';
import './styles/main.scss';

const App = () => {
  // State
  const [selectedCity, setSelectedCity] = useState(null);
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [availableCities, setAvailableCities] = useState([]);
  const [allViewedCities, setAllViewedCities] = useLocalStorage('allViewedCities', []);
  const [popularCities, setPopularCities] = useLocalStorage('popularCities', []);
// Update all viewed cities first
setAllViewedCities(prevAll => {
  const updatedAll = [...prevAll];
  const existingIndex = updatedAll.findIndex(c => c.code === selectedCity.code);
  
  if (existingIndex >= 0) {
    updatedAll[existingIndex] = {
      ...updatedAll[existingIndex],
      views: (updatedAll[existingIndex].views || 0) + 1,
      lastViewed: new Date().toISOString()
    };
  } else {
    updatedAll.push({
      ...selectedCity,
      views: 1,
      lastViewed: new Date().toISOString()
    });
  }
  return updatedAll;
});

// Then derive the popular cities from all viewed cities
setPopularCities(prev => {
  const sorted = [...allViewedCities].sort((a, b) => {
    if (b.views !== a.views) return b.views - a.views;
    return new Date(b.lastViewed) - new Date(a.lastViewed);
  });
  return sorted.slice(0, 3);
});  const [loading, setLoading] = useState({ cities: false, weather: false });
  const [error, setError] = useState({ cities: null, weather: null });

  // Load cities once on component mount
  useEffect(() => {
    const loadCities = async () => {
      try {
        setLoading(prev => ({ ...prev, cities: true }));
        setError(prev => ({ ...prev, cities: null }));
        const cities = await fetchCities();
        setAvailableCities(cities);
      } catch (err) {
        setError(prev => ({ ...prev, cities: err.message || 'Failed to load cities' }));
        console.error('Cities load error:', err);
      } finally {
        setLoading(prev => ({ ...prev, cities: false }));
      }
    };
    loadCities();
  }, []);

  // Process weather data from API
  const processWeatherData = (apiData) => {
    if (!apiData) throw new Error('No data received');
    
    // Backend already processes the data, so we can use it directly
    if (typeof apiData === 'object' && !Array.isArray(apiData)) {
      return apiData;
    }
    
    throw new Error('Unsupported data format');
  };

  // Load weather data when city is selected
  useEffect(() => {
    if (selectedCity) {
      const loadWeatherData = async () => {
        try {
          setLoading(prev => ({ ...prev, weather: true }));
          setError(prev => ({ ...prev, weather: null }));
          
          const rawData = await fetchWeather(selectedCity.code);
          const processedData = processWeatherData(rawData);
          
          // Set forecast data
          setForecastData(processedData);
          
          // Extract current weather from the first available data point
          const firstDate = Object.keys(processedData)[0];
          if (firstDate) {
            const firstHour = Object.keys(processedData[firstDate])[0];
            if (firstHour) {
              setCurrentWeather({
                airTemperature: processedData[firstDate][firstHour].temperature,
                conditionCode: processedData[firstDate][firstHour].condition,
                windSpeed: processedData[firstDate][firstHour].windSpeed,
                relativeHumidity: processedData[firstDate][firstHour].humidity || 0
              });
            }
          }

          // Update popular cities list
          // Update popular cities list
          // Update popular cities list
          // Update all viewed cities first
          setAllViewedCities(prevAll => {
            const updatedAll = [...prevAll];
            const existingIndex = updatedAll.findIndex(c => c.code === selectedCity.code);
            
            if (existingIndex >= 0) {
              updatedAll[existingIndex] = {
                ...updatedAll[existingIndex],
                views: (updatedAll[existingIndex].views || 0) + 1,
                lastViewed: new Date().toISOString()
              };
            } else {
              updatedAll.push({
                ...selectedCity,
                views: 1,
                lastViewed: new Date().toISOString()
              });
            }
            return updatedAll;
          });

          // Then derive the popular cities from all viewed cities
          setPopularCities(prev => {
            const sorted = [...allViewedCities].sort((a, b) => {
              if (b.views !== a.views) return b.views - a.views;
              return new Date(b.lastViewed) - new Date(a.lastViewed);
            });
            return sorted.slice(0, 3);
          });

          // Log city selection
          await logCitySelection(selectedCity);
        } catch (err) {
          setError(prev => ({ ...prev, weather: err.message || 'Failed to load weather data' }));
          console.error('Weather load error:', {
            error: err,
            city: selectedCity,
            time: new Date().toISOString()
          });
        } finally {
          setLoading(prev => ({ ...prev, weather: false }));
        }
      };

      loadWeatherData();
    }
  }, [selectedCity]);

  return (
    <div className="app-container">
      <header>
        <h1>Weather Forecast</h1>
      </header>
      
      <main>
        {/* City Selection Section */}
        <div className="search-section">
          <CityDropdown 
            cities={availableCities}
            onSelect={setSelectedCity}
            loading={loading.cities}
            error={error.cities}
          />
          
          {popularCities.length > 0 && (
            <PopularCities 
              cities={popularCities} 
              onSelect={setSelectedCity} 
            />
          )}
        </div>

        {/* Loading and Error States */}
        {loading.weather && (
          <div className="loading">Loading weather data...</div>
        )}

        {error.weather && (
          <div className="error">
            <p>{error.weather}</p>
            <button 
              onClick={() => setSelectedCity({ ...selectedCity })}
              className="retry-btn"
            >
              Retry
            </button>
          </div>
        )}

        {/* Weather Display */}
        {selectedCity && !loading.weather && !error.weather && (
          <>
            {currentWeather && (
              <CurrentWeather 
                weather={currentWeather} 
                cityName={selectedCity.name} 
              />
            )}
            
            {forecastData && (
                <HourlyForecast 
                forecastData={forecastData} 
                cityName={selectedCity.name}  // Pass the city name
              />
            )}
          </>
        )}
      </main>
    </div>
  );
};

export default App;
