export const fetchCities = async () => {
    const response = await fetch('https://api.meteo.lt/v1/places');
    return await response.json();
  };
  
  export const fetchWeather = async (cityCode) => {
    const response = await fetch(`https://api.meteo.lt/v1/places/${cityCode}/forecasts/long-term`);
    return await response.json();
  };
  
  export const logCitySelection = async (city) => {
    await fetch('/api/logs', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ city: city.name, timestamp: new Date().toISOString() }),
    });
  };
