import React, { useState, useEffect } from 'react';
import { fetchCities, fetchWeather, logCitySelection } from './utils/api';
import CityDropdown from './components/CityDropdown';
import CurrentWeather from './components/CurrentWeather';
import HourlyForecast from './components/HourlyForecast';
import PopularCities from './components/PopularCities';
import useLocalStorage from './hooks/useLocalStorage';
import './styles/main.scss';

const App = () => {
  // State definitions
  const [selectedCity, setSelectedCity] = useState(null);
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [availableCities, setAvailableCities] = useState([]);
  const [popularCities, setPopularCities] = useLocalStorage('popularCities', []);
  const [loading, setLoading] = useState({ cities: false, weather: false });
  const [error, setError] = useState({ cities: null, weather: null });

  // Load available cities on first render
  useEffect(() => {
    const loadCities = async () => {
      try {
        setLoading(prev => ({ ...prev, cities: true }));
        setError(prev => ({ ...prev, cities: null }));
        
        const cities = await fetchCities();
        setAvailableCities(cities);
        
        // Auto-select the first popular city if available
        if (popularCities.length > 0 && !selectedCity) {
          setSelectedCity(popularCities[0]);
        }
      } catch (err) {
        setError(prev => ({ ...prev, cities: 'Failed to load cities list' }));
        console.error('Cities load error:', err);
      } finally {
        setLoading(prev => ({ ...prev, cities: false }));
      }
    };

    loadCities();
  }, []);

  // Process weather data into days and hours
  const processWeatherData = (apiData) => {
    // Validate basic structure
    if (!apiData || !apiData.forecastTimestamps || !Array.isArray(apiData.forecastTimestamps)) {
      throw new Error('API response missing forecastTimestamps array');
    }

    // Check for minimum data
    if (apiData.forecastTimestamps.length === 0) {
      throw new Error('Empty forecast data received');
    }

    const days = {};
    let validEntries = 0;

    apiData.forecastTimestamps.forEach(entry => {
      try {
        // Validate required fields
        if (!entry.forecastTimeUtc || typeof entry.airTemperature === 'undefined') {
          throw new Error('Missing required weather fields');
        }

        const [date, time] = entry.forecastTimeUtc.split(' ');
        const hour = time.substring(0, 5); // Get HH:MM format
        
        if (!days[date]) days[date] = {};
        
        days[date][hour] = {
          temp: entry.airTemperature,
          condition: entry.conditionCode || 'N/A',
          wind: entry.windSpeed || 'N/A',
          humidity: entry.relativeHumidity || 'N/A'
        };
        validEntries++;
      } catch (entryError) {
        console.warn('Skipping invalid forecast entry:', {
          error: entryError.message,
          entry
        });
      }
    });

    // Verify we have sufficient data
    if (validEntries < 24) { // At least 24 hours of data
      throw new Error(`Insufficient valid forecast data (${validEntries} entries)`);
    }

    return days;
  };

  // Load weather when city is selected
  useEffect(() => {
    if (selectedCity) {
      const loadWeatherData = async () => {
        try {
          setLoading(prev => ({ ...prev, weather: true }));
          setError(prev => ({ ...prev, weather: null }));
          
          const rawData = await fetchWeather(selectedCity.code);
          const processedData = processWeatherData(rawData);
          
          setCurrentWeather(rawData.forecastTimestamps[0]);
          setForecastData(processedData);
          
          // Update popular cities
          setPopularCities(prev => {
            const updated = [...prev];
            const existingIndex = updated.findIndex(c => c.code === selectedCity.code);
            
            if (existingIndex >= 0) {
              updated[existingIndex].views = (updated[existingIndex].views || 0) + 1;
            } else {
              updated.push({ ...selectedCity, views: 1 });
            }
            
            return updated
              .sort((a, b) => b.views - a.views)
              .slice(0, 3);
          });

          await logCitySelection(selectedCity);
        } catch (err) {
          setError(prev => ({ 
            ...prev, 
            weather: `Failed to load weather: ${err.message}`
          }));
          console.error('Weather processing failed:', {
            error: err,
            city: selectedCity,
            time: new Date().toISOString()
          });
        } finally {
          setLoading(prev => ({ ...prev, weather: false }));
        }
      };

      loadWeatherData();
    }
  }, [selectedCity]);

  return (
    <div className="app-container">
      <header>
        <h1>Weather Forecast</h1>
      </header>
      
      <main>
        {/* City Selection Section */}
        <div className="search-section">
          <CityDropdown 
            cities={availableCities}
            onSelect={setSelectedCity} 
            popularCities={popularCities}
            loading={loading.cities}
            error={error.cities}
          />
          
          {popularCities.length > 0 && (
            <PopularCities 
              cities={popularCities} 
              onSelect={setSelectedCity} 
            />
          )}
        </div>

        {/* Loading and Error States */}
        {loading.weather && (
          <div className="loading">
            <div className="spinner"></div>
            Loading weather data...
          </div>
        )}
        
        {error.weather && (
          <div className="error">
            <p>{error.weather}</p>
            <button 
              onClick={() => setSelectedCity({ ...selectedCity })} // Force re-fetch
              className="retry-button"
            >
              â†» Retry
            </button>
          </div>
        )}

        {/* Weather Display Section */}
        {selectedCity && !loading.weather && !error.weather && (
          <>
            {currentWeather && (
              <CurrentWeather 
                weather={currentWeather} 
                cityName={selectedCity.name} 
              />
            )}
            
            {forecastData && (
              <>
                <HourlyForecast forecastData={forecastData} />
                
                {/* Debug view in development */}
                {process.env.NODE_ENV === 'development' && (
                  <details className="debug-view">
                    <summary>Raw API Data</summary>
                    <pre>{JSON.stringify({
                      selectedCity,
                      currentWeather,
                      forecastSample: Object.keys(forecastData).map(date => ({
                        date,
                        hours: Object.keys(forecastData[date]).length
                      }))
                    }, null, 2)}</pre>
                  </details>
                )}
              </>
            )}
          </>
        )}
      </main>
    </div>
  );
};

export default App;
