import React, { useState, useEffect } from 'react';
import { fetchCities, fetchWeather, logCitySelection } from './utils/api';
import CityDropdown from './components/CityDropdown';
import CurrentWeather from './components/CurrentWeather';
import Forecast from './components/Forecast';
import PopularCities from './components/PopularCities';
import './styles/main.scss';
import useLocalStorage from './hooks/useLocalStorage';
import HourlyForecast from './components/HourlyForecast';            <CurrentWeather weather={weather} />

const App = () => {
  const [selectedCity, setSelectedCity] = useState(null);
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [popularCities, setPopularCities] = useLocalStorage('popularCities', []);

  useEffect(() => {
    if (selectedCity) {
      const loadWeather = async () => {
        const data = await fetchWeather(selectedCity.code);
        setWeather(data.forecastTimestamps[0]);
        setForecast(data.forecastTimestamps);
        
        // Update popular cities
        setPopularCities(prev => {
          const existingIndex = prev.findIndex(c => c.code === selectedCity.code);
          if (existingIndex >= 0) {
            const updated = [...prev];
            updated[existingIndex].views = (updated[existingIndex].views || 0) + 1;
            return updated.sort((a, b) => b.views - a.views).slice(0, 3);
          }
          return [...prev, { ...selectedCity, views: 1 }]
            .sort((a, b) => b.views - a.views)
            .slice(0, 3);
        });

        await logCitySelection(selectedCity);
      };
      loadWeather();
    }
  }, [selectedCity]);

  return (
    <div className="app-container">
      <header>
        <h1>Weather Forecast</h1>
      </header>
      <main>
        <div className="search-section">
          <CityDropdown onSelect={setSelectedCity} popularCities={popularCities} />
          {popularCities.length > 0 && (
            <PopularCities cities={popularCities} onSelect={setSelectedCity} />
          )}
        </div>
        
        {selectedCity && (
          <>
            <CurrentWeather weather={weather} />
            <HourlyForecast forecastData={forecast} />
          </>
        )}
      </main>
    </div>
  );
};

export default App;
